import './App.css';
import React, { Component } from 'react';
import TestList from "./components/TestList";
import Content from "./components/Content";
import Nav from "./components/Nav";
import Profile from "./components/Profile";

//상위->하위 : props
//하위->상위 : event

class App extends Component{
  constructor(props){ // 컴포넌트 초기화
    super(props);
    this.state ={
      mode: 'read',
      selected_content_id:2,
      nav:{title:'Ask for Question'},
      contents:[
        {id:1, title:'Test1', date: 'HTML is for information'},
        {id:2, title:'Test2', date: 'CSS is for design'},
        {id:3, title:'Test3', date: 'JavaScript is for interaction'},
      ]
    }
  }
  render() {
    console.log('App render');
    var _title, _desc = null;
    if(this.state.mode === 'welcome'){
      _title = this.state.welcome.title;
      _desc = this.state.welcome.desc;
    } else if(this.state.mode === 'read'){
      var i =0;
      while(i < this.state.contents.length){
        var data = this.state.contents[i];
        if(data.id == this.state.selected_content_id){
          _title = data.title;
          _desc = data.desc;
          break;
        }
        i = i+1;
      }
    }
    console.log('render', this)
    return (
      <div className="App">
       <Nav
        title={this.state.nav.title}
        onChangePage={function() {
          this.setState({mode:'welcome'})
        }.bind(this)}>
       </Nav>
       <div display="flex">
         <Profile></Profile>
         <TestList onChangePage={function(id) {
         this.setState({
           mode:'read',
           selected_content_id:Number(id)
          });
       }.bind(this)}
       data={this.state.contents}
       ></TestList>
       </div>

       <Content title={_title} desc={_desc}>
       </Content>

      </div>
    );
  }
}


export default App;
